import React from 'react';

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'

let live = 'live';
let offline = 'offline';

class StreamInfo extends React.Component {

    state = { [this.props.user]: offline };

    componentDidMount() {
        this.checkLive(this.props.user)
    }
    
    checkLive = async streamer => {
        let thisState = this

        api.streams.getStreams({user_login: streamer}).then(function(data) {
            if(data.response.data.length > 0) {
                for(let i = 0; i < data.response.data.length; i++) {
                    let un = data.response.data[i].user_name.toLowerCase();

                    thisState.setState({[un]: live});
                }
            }
        });
    }

    render(){
        return(
            <div className="stream-container">
                <div>{this.props.user.toUpperCase()}</div>
                <div>{this.state[this.props.user]}</div>
            </div>
        )
    }
    
}

export default StreamInfo;
