import React from 'react';
import './css/StreamInfo.css';

import twittersvg from './images/twitter.svg';
import youtubesvg from './images/youtube.svg';

let xhttp = new XMLHttpRequest;

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'

let live = 'Live';
let offline = 'Offline';
let status = offline;

let streamInfo;

class StreamInfo extends React.Component {

    state = { [this.props.user[0]]: offline, game: undefined, title: undefined};

    componentDidMount() {
        loadXML = () => {

            var xhttp = new XMLHttpRequest();
    
            xhttp.onreadystatechange = function() {
                if (this.readyState == 4 && this.status == 200) {
    
                    var xmlDoc = this.responseXML;
                    var y = xmlDoc.getElementsByTagName("newFile");
    
                    for(var i=0; i<y.length; i++) {
    
                        var NameArray = [y[i].getElementsByTagName("FileName")[0]];
                    }
    
                    const nameList = NameArray.map(name => 
                        <li>{name}</li>
                    );
                }
            };
    
            xhttp.open("GET", "./FilenameList.xml", true);
            xhttp.send();
        }    

        this.checkLive(this.props.user[0])
    }
    
    checkLive = async streamer => {
        let thisState = this

        api.streams.getStreams({user_login: streamer}).then(function(data) {
            if(data.response.data[0]) {
                streamInfo = data.response.data[0];
                let un = streamInfo.user_name.toLowerCase();

                thisState.setState({title: streamInfo.title})

                api.games.getGames({id: streamInfo.game_id}).then(function(data) {
                    thisState.setState({game: data.response.data[0].name})
                })

                status = live;
                
                thisState.setState({[un]: live});
            }
        });
    }
 
    getGame = () => {
        let thisState = this

        if(status === live) {
            return <div className="stream-info">{thisState.game}</div>
        }

        else {
            return (
                <a class="twitter-timeline"
                    href="https://twitter.com/northernlionlp"
                    data-width="250"
                    data-height="150">
                </a>
            )
        }
        
    }    

    render(){
        return(
            <div className={'stream-container bold center ' + status}>
                <a href={'https://twitch.tv/' + this.props.user[0]}><h1 className='user'>{this.props.user[0].toUpperCase()}</h1></a>
                <div className="stream-info-container">
                    <a href={'https://twitter.com/' + this.props.user[1]}><img className="icon" src={twittersvg} alt="twitter"/> </a>
                    <div>{this.state[this.props.user[0]]}</div>
                    <a href={'https://youtube.com/' + this.props.user[2]}><img className="icon" src={youtubesvg} alt="youtube" /></a>
                </div>
                
                <hr />
                <div className="stream-title">{this.state.title}</div>
                <div className="stream-title">{this.state.game}</div>
            </div>
        )
    }
    
}

export default StreamInfo;
