    'jsmithoti',
    'alpacapatrol',
    'cobaltstreak',
    'baertaffy',
    'dangheesling',
    'sinvicta',
    'lovelymomo',
    'mathas',
    'flackblag',
    'hcjustin',
    'itshafu'
]

class App extends React.Component {
    
    state = { northernlion: offline, rockleesmile: offline, dumbdog: offline, michaelalfox: offline, jsmithoti: offline, alpacapatrol: offline, cobaltstreak: offline, baertaffy: offline, dangheesling: offline, sinvicta: offline, lovelymomo: offline, mathas: offline, flackblag: offline, hcjustin: offline, itshafu: offline }

    componentDidMount = async () => {
        const response = await twitch.get( {
            params:{ login: streamers.join('&login=')}
        })
        console.log(response)
    }

    render() {
        return(
            <div>
                <div id="countdown">this is a countdown</div>
                <div id="people-container">

                </div>
            </div>
        )
    }
}

export default App;
