import React from 'react';
import './css/StreamInfo.css';

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'
// b2db5ebd1658eef596137314b88ce2fe25a9e621
let live = 'Live';
let offline = 'Offline';
let status = offline;

let twitch = props.user[0];
let twitter = props.user[1];
let youtube = props.user[2];

let streamInfo;

class StreamInfo extends React.Component {

    state = { [twitch]: offline, game: undefined, title: undefined};

    componentDidMount() {
        this.checkLive(twitch)
    }
    
    checkLive = async streamer => {
        let thisState = this

        api.streams.getStreams({user_login: streamer}).then(function(data) {
            if(data.response.data[0]) {
                streamInfo = data.response.data[0];
                let un = streamInfo.user_name.toLowerCase();

                thisState.setState({title: streamInfo.title})

                api.games.getGames({id: streamInfo.game_id}).then(function(data) {
                    thisState.setState({game: data.response.data[0].name})
                })

                status = live;
                
                thisState.setState({[un]: live});
            }
        });
    }
 
    getGame = () => {
        let thisState = this

        if(status === live) {
            return <div className="stream-info">{thisState.game}</div>
        }
    }    

    render(){
        return(
            <div className={'stream-container ' + status}>
                <a href={'https://twitch.tv/' + this.props.user}><h1 className='user'>{this.props.user.toUpperCase()}</h1></a>
                <div>{this.state[this.props.user]}</div>
                <hr />
                <div className="stream-title">{this.state.title}</div>
                <div className="stream-title">{this.state.game}</div>
            </div>
        )
    }
    
}

export default StreamInfo;
