import React from 'react';
import './css/StreamInfo.css';

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'

let live = 'Live';
let offline = 'Offline';
let status = offline;

let streamInfo;

class StreamInfo extends React.Component {

    state = { [this.props.user]: offline };

    componentDidMount() {
        this.checkLive(this.props.user)
    }
    
    checkLive = async streamer => {
        let thisState = this

        api.streams.getStreams({user_login: streamer}).then(function(data) {
            if(data.response.data[0]) {
                streamInfo = data.response.data[0];
                let un = streamInfo.user_name.toLowerCase();
                
                console.log(data.response.data[0])
                status = live;
                
                thisState.setState({[un]: live});
            }
        });
    }

    isLive = () => {
        if(status === live) {
            return <div className="stream-title">{streamInfo.title}</div>
        }
    }

    render(){
        return(
            <div className={'stream-container ' + status}>
                <h1>{this.props.user.toUpperCase()}</h1>
                <div>{this.state[this.props.user]}</div>
                {this.isLive()}
            </div>
        )
    }
    
}

export default StreamInfo;
