import React from 'react';

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'

let live = 'live';
let offline = 'offline';
let streamers = [
    'northernlion',
    'rockleesmile',
    'dumbdog',
    'michaelalfox',
    'jsmithoti',
    'alpacapatrol',
    'cobaltstreak',
    'baertaffy',
    'dangheesling',
    'sinvicta',
    'lovelymomo',
    'mathas',
    'flackblag',
    'hcjustin',
    'itshafu'
]

class App extends React.Component {
    
    state = { northernlion: offline, rockleesmile: offline, dumbdog: offline, michaelalfox: offline, jsmithoti: offline, alpacapatrol: offline, cobaltstreak: offline, baertaffy: offline, dangheesling: offline, sinvicta: offline, lovelymomo: offline, mathas: offline, flackblag: offline, hcjustin: offline, itshafu: offline }

    componentDidMount() {
        this.checkLive(streamers.join('&user_login='))
    }
    
    checkLive = async allStreams => {
        let thisState = this

        console.log(allStreams);
        console.log('dumbdog&user_login=northernlion')
        api.streams.getStreams({user_login: 'dumbdog&user_login=northernlion'}).then(function(data) {
            if(data.response.data.length > 0) {
                for(let i = 0; i < data.response.data.length; i++) {
                    let un = data.response.data[i].user_name.toLowerCase();

                    thisState.setState({[un]: live});
                }
            }
        });
    }
    onClick = () => {
        console.log(this.state)
    }
    render() {
        return(
            <div>
                <div onClick={() => this.onClick()}id="countdown">this is a countdown</div>
                <div id="people-container">

                </div>
            </div>
        )
    }
}

export default App;
