import React from 'react';

const api = require('twitch-helix-api');

api.clientID = 'ew5y8dnvd35deca2cqe9ap0ol4hgjy'

let live = 'live';
let offline = 'offline';
let streamers = [
    'northernlion',
    'rockleesmile',
    'dumbdog',
    'michaelalfox',
    'jsmithoti',
    'alpacapatrol',
    'cobaltstreak',
    'baertaffy',
    'dangheesling',
    'sinvicta',
    'lovelymomo',
    'mathas',
    'flackblag',
    'hcjustin',
    'itshafu'
]

class App extends React.Component {
    
    state = { northernlion: offline, rockleesmile: offline, dumbdog: offline, michaelalfox: offline, jsmithoti: offline, alpacapatrol: offline, cobaltstreak: offline, baertaffy: offline, dangheesling: offline, sinvicta: offline, lovelymomo: offline, mathas: offline, flackblag: offline, hcjustin: offline, itshafu: offline }

    componentDidMount() {
        // this.checkLive(streamers.join('&login='))
        this.checkLive('dumbdog')
    }
    
    checkLive = async streams => {
        let un = []
        // api.streams.getStreams({user_login: streamers.join('&user_login=')}).then(function(data) {
        api.streams.getStreams({user_login: 'dumbdog'}).then(function(data) {
            
            if(data.response.data.length > 0) {
                for(let i = 0; i < data.response.data.length; i++) {
                    un.push(data.response.data[i].user_name)
                }
            }
        });
        
        return un;
    }

    render() {
        return(
            <div>
                <div id="countdown">this is a countdown</div>
                <div id="people-container">

                </div>
            </div>
        )
    }
}

export default App;
